package datatypes;

public class ArithmeticOperations {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int number1=2;
		int number2=3;
		
		int add=number1+number2;
		
		System.out.println("ADDITION OF TWO NUMBERS IS : "+add);
		
		float num3=3.4f;
		float num4=6.8f;
		
		float sub=number1-number2;
		
		System.out.println("Subtraction of two numbers is : "+sub);
		
		long num5=45678912345678123l;
		long num6=763541178872298365l; 
		
		
		long multiply=number1*number2;
		
		System.out.println("Multiplication of two numbers is :"+multiply);
		
	
		
		double num7=5678.99999999999999d;
		double num8=6789.33333333333333d;
		
		
		double quotient=num7/num8;
		
		System.out.println("QUOTIENT OF THE TWO NUMBERS ARE : "+quotient);
	
		
		int num9=66;
		int num10=6;
		
		int rem=num9%num10;
		
		System.out.println("Remainder of the two nos are : "+rem);
		
		
	
	}

}
