package datatypes;

public class ComputerDetails {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int modelNo=1234;
		String brandName="DELL";
		long ipAddress=123456789101112l;
		float heightInCm=27.3f;
		double weightInPounds=5.34567893456782345d;
		char version='6';
		boolean flagshipSale=true;
		
		
		System.out.println("COMPUTER DETAILS ");
		System.out.println();
		System.out.println("MODEL NO : "+modelNo);
		
		System.out.println("BRAND : "+brandName);
		
		System.out.println("IP ADDRESS : "+ipAddress);
		
		System.out.println("HEIGHT in CM : "+heightInCm);
		
		System.out.println("WEIGHT in Pounds : "+weightInPounds);
		
		System.out.println("VERSION or Generation in processor : "+version);
		
		System.out.println("Is That Available For a FlagShipSale : "+flagshipSale);
		
	}

}
