package datatypes;

public class DisplayingStudentDetails {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int rollNo=24;
		String name="MOHAMED AZEEM ";
		char initial='S';
		float height=5.28f;
		long mobileNo=9444802806l;
		double weightInGrams=6.5555555555555555555555d;
		boolean indian=true;
		
		System.out.println("DISPLAYING A STUDENT's DETAILS");
		System.out.println();
		System.out.println("ROLL NO : "+rollNo );
		
		System.out.println("NAME : "+name);
		
		System.out.println("Initial : "+initial);
		
		System.out.println("HEIGHT in Inches : "+height);
		
		System.out.println("MOBILE NO : "+mobileNo);
		
		System.out.println("WEIGHT in Grams : "+weightInGrams);
		
		System.out.println("NATIONALITY = INDIAN ? "+indian);

	}

}
