package SwitchCase_Conditions;

public class MonthCounting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String month="MAY";
		
		switch(month) {
		case "JANUARY":
			System.out.println("First Month");
			break;
		
		case "FEBRARY":
			System.out.println("Second Month");
			break;
		case "MARCH":
			System.out.println("Third Month");
			break;
		case "APRIL":
			System.out.println("Fourth Month");
			break;
		case "MAY":
			System.out.println("Fifth Month");
			break;
		case "JUNE":
			System.out.println("Sixth Month");
			break;
		case "July":
			System.out.println("Seventh Month");
			break;
		case "AUGUST":
			System.out.println("Eighth Month");
			break;
		case "SEPTEMBER":
			System.out.println("Nineth Month");
			break;
		case "OCTOBER":
			System.out.println("Tenth Month");
			break;
		case "NOVEMBER":
			System.out.println("Eleventh Month");
			break;
		case "DECEMBER":
			System.out.println("Twelfth Month");
			break;
			default:
				System.out.println("IN VALID DATA/////");
		}
		
	}

		
}
