package if_Conditions;

public class PositiveValue {

	public static void main(String[] args) {              //logic positive=number>0;negative=number<0;
		// TODO Auto-generated method stub

		int number=-5;
		
		if(number>0) {
			
			System.out.println(number+10);
		}else
	
		System.out.println(number-10);
		
		
	}

}
