package lmsRevision;

public class FibonacciSeries {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int n1=0,n2=1;
		//total=0;
		System.out.println(n1);
		System.out.println(n2);
		for(int i=2;i<10;i++) {
			
			int n3=n1+n2;
			System.out.print(n3+" ");
			n1=n2;
			n2=n3;
			//int total=total+n3;
		}
	
	
	
	
	
	
	}

}
