package lmsRevision;

public class OddOrEvenChecking {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num=15;
		if(num%2==0) {
			System.out.println("EVEN NUMBER...");
		}
		else if(num%2!=0) {
			System.out.println("ODD NUMBER...");
		}
		else {
			System.out.println("Its NEUTRAL NUMBER.....");
		}
	}

}
