package lmsRevision;

public class SwitchColorChecking {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	String color="White";
		switch(color) {
		case "Red":
			System.out.println("This is red color ......");
			break;
		case "Blue":
			System.out.println("This is blue color......");
			break;
		case "Green":
			System.out.println("This is green color......");
			break;
		case "Yellow":
			System.out.println("This is Yellow color.....");
			break;
		case "White":
			System.out.println("This is White color......");
			break;
		case "Gold":
			System.out.println("This is gold color.......");
			break;
			default :
				System.out.println("Invalid color name");
		}
	}
	
}


